import React, { useEffect, useState } from "react";
import {
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Typography,
  Box,
  CircularProgress,
} from "@mui/material";
import axios from "axios";

interface Player {
  _id: string;
  name: string;
  stats: {
    totalKills: number;
    totalAssists: number;
    totalDeaths: number;
    totalMatches: number;
    winRate: number;
    averageKd: number;
    averageImpact: number;
    mapStats: Array<{
      mapName: string;
      matches: number;
      wins: number;
      winRate: number;
    }>;
  };
}

const PlayerStats: React.FC = () => {
  const [players, setPlayers] = useState<Player[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchPlayers = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/players");
        setPlayers(response.data);
        setLoading(false);
      } catch (error) {
        console.error("Error fetching players:", error);
        setLoading(false);
      }
    };

    fetchPlayers();
  }, []);

  if (loading) {
    return (
      <Box
        display="flex"
        justifyContent="center"
        alignItems="center"
        minHeight="100vh"
      >
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Box p={3}>
      <Typography variant="h4" gutterBottom>
        Статистика команды
      </Typography>
      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>Игрок</TableCell>
              <TableCell align="right">Матчи</TableCell>
              <TableCell align="right">Убийства</TableCell>
              <TableCell align="right">Помощи</TableCell>
              <TableCell align="right">Смерти</TableCell>
              <TableCell align="right">K/D</TableCell>
              <TableCell align="right">Импакт</TableCell>
              <TableCell align="right">Винрейт</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {players.map((player) => (
              <TableRow key={player._id}>
                <TableCell component="th" scope="row">
                  {player.name}
                </TableCell>
                <TableCell align="right">{player.stats.totalMatches}</TableCell>
                <TableCell align="right">{player.stats.totalKills}</TableCell>
                <TableCell align="right">{player.stats.totalAssists}</TableCell>
                <TableCell align="right">{player.stats.totalDeaths}</TableCell>
                <TableCell align="right">
                  {player.stats.averageKd.toFixed(2)}
                </TableCell>
                <TableCell align="right">
                  {player.stats.averageImpact.toFixed(2)}
                </TableCell>
                <TableCell align="right">
                  {player.stats.winRate.toFixed(1)}%
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </Box>
  );
};

export default PlayerStats;
