import React, { useState } from "react";
import {
  Box,
  Button,
  TextField,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Paper,
  Typography,
} from "@mui/material";
import axios from "axios";

interface Player {
  id: string;
  name: string;
  matches: any[];
  stats: {
    totalKills: number;
    totalAssists: number;
    totalDeaths: number;
    totalMatches: number;
    winRate: number;
    averageKd: number;
    averageImpact: number;
    mapStats: any[];
  };
}

interface AddMatchProps {
  players: Player[];
  onMatchAdded: () => void;
}

const AddMatch: React.FC<AddMatchProps> = ({ players, onMatchAdded }) => {
  const [selectedPlayer, setSelectedPlayer] = useState("");
  const [map, setMap] = useState("");
  const [kills, setKills] = useState("");
  const [assists, setAssists] = useState("");
  const [deaths, setDeaths] = useState("");
  const [win, setWin] = useState("");

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    try {
      await axios.post(
        `http://localhost:5000/api/players/${selectedPlayer}/matches`,
        {
          map,
          kills: parseInt(kills),
          assists: parseInt(assists),
          deaths: parseInt(deaths),
          win: win === "true",
        }
      );

      // Сброс формы
      setSelectedPlayer("");
      setMap("");
      setKills("");
      setAssists("");
      setDeaths("");
      setWin("");

      onMatchAdded();
    } catch (error) {
      console.error("Error adding match:", error);
    }
  };

  return (
    <Paper sx={{ p: 3, mb: 3 }}>
      <Typography variant="h6" gutterBottom>
        Добавить матч
      </Typography>
      <Box
        component="form"
        onSubmit={handleSubmit}
        sx={{ display: "flex", flexDirection: "column", gap: 2 }}
      >
        <FormControl fullWidth required>
          <InputLabel>Игрок</InputLabel>
          <Select
            value={selectedPlayer}
            label="Игрок"
            onChange={(e) => setSelectedPlayer(e.target.value)}
          >
            {players.map((player) => (
              <MenuItem key={player.id} value={player.id}>
                {player.name}
              </MenuItem>
            ))}
          </Select>
        </FormControl>

        <TextField
          label="Карта"
          value={map}
          onChange={(e) => setMap(e.target.value)}
          required
          fullWidth
        />

        <TextField
          label="Убийства"
          type="number"
          value={kills}
          onChange={(e) => setKills(e.target.value)}
          required
          fullWidth
        />

        <TextField
          label="Помощи"
          type="number"
          value={assists}
          onChange={(e) => setAssists(e.target.value)}
          required
          fullWidth
        />

        <TextField
          label="Смерти"
          type="number"
          value={deaths}
          onChange={(e) => setDeaths(e.target.value)}
          required
          fullWidth
        />

        <FormControl fullWidth required>
          <InputLabel>Результат</InputLabel>
          <Select
            value={win}
            label="Результат"
            onChange={(e) => setWin(e.target.value)}
          >
            <MenuItem value="true">Победа</MenuItem>
            <MenuItem value="false">Поражение</MenuItem>
          </Select>
        </FormControl>

        <Button
          type="submit"
          variant="contained"
          color="primary"
          fullWidth
          disabled={
            !selectedPlayer || !map || !kills || !assists || !deaths || !win
          }
        >
          Добавить матч
        </Button>
      </Box>
    </Paper>
  );
};

export default AddMatch;
