import React, { useState } from "react";
import {
  Box,
  Button,
  TextField,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Paper,
  Typography,
} from "@mui/material";
import axios from "axios";

interface AddMatchProps {
  players: Array<{
    _id: string;
    name: string;
  }>;
  onMatchAdded: () => void;
}

const AddMatch: React.FC<AddMatchProps> = ({ players, onMatchAdded }) => {
  const [selectedPlayer, setSelectedPlayer] = useState("");
  const [map, setMap] = useState("");
  const [kills, setKills] = useState("");
  const [assists, setAssists] = useState("");
  const [deaths, setDeaths] = useState("");
  const [win, setWin] = useState("");

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    try {
      await axios.post(
        `http://localhost:5000/api/players/${selectedPlayer}/matches`,
        {
          map,
          kills: parseInt(kills),
          assists: parseInt(assists),
          deaths: parseInt(deaths),
          win: win === "true",
        }
      );

      // Сброс формы
      setSelectedPlayer("");
      setMap("");
      setKills("");
      setAssists("");
      setDeaths("");
      setWin("");

      onMatchAdded();
    } catch (error) {
      console.error("Error adding match:", error);
    }
  };

  return (
    <Paper sx={{ p: 3, mb: 3 }}>
      <Typography variant="h6" gutterBottom>
        Добавить матч
      </Typography>
      <Box component="form" onSubmit={handleSubmit}>
        <FormControl fullWidth sx={{ mb: 2 }}>
          <InputLabel>Игрок</InputLabel>
          <Select
            value={selectedPlayer}
            onChange={(e) => setSelectedPlayer(e.target.value)}
            required
          >
            {players.map((player) => (
              <MenuItem key={player._id} value={player._id}>
                {player.name}
              </MenuItem>
            ))}
          </Select>
        </FormControl>

        <TextField
          fullWidth
          label="Карта"
          value={map}
          onChange={(e) => setMap(e.target.value)}
          required
          sx={{ mb: 2 }}
        />

        <TextField
          fullWidth
          label="Убийства"
          type="number"
          value={kills}
          onChange={(e) => setKills(e.target.value)}
          required
          sx={{ mb: 2 }}
        />

        <TextField
          fullWidth
          label="Помощи"
          type="number"
          value={assists}
          onChange={(e) => setAssists(e.target.value)}
          required
          sx={{ mb: 2 }}
        />

        <TextField
          fullWidth
          label="Смерти"
          type="number"
          value={deaths}
          onChange={(e) => setDeaths(e.target.value)}
          required
          sx={{ mb: 2 }}
        />

        <FormControl fullWidth sx={{ mb: 2 }}>
          <InputLabel>Результат</InputLabel>
          <Select value={win} onChange={(e) => setWin(e.target.value)} required>
            <MenuItem value="true">Победа</MenuItem>
            <MenuItem value="false">Поражение</MenuItem>
          </Select>
        </FormControl>

        <Button type="submit" variant="contained" color="primary" fullWidth>
          Добавить матч
        </Button>
      </Box>
    </Paper>
  );
};

export default AddMatch;
